mapper.select:
select

from DATA_QUALITY_RULE a


    a.DEL_FLAG = 0

        AND  a.ORGANIZER_ID  = #{organizerId}


        AND  a.DATABASE_ID   = #{databaseId}


        AND  a.RULE_TYPE   = #{ruleType}


        AND  a.TABLE_NAME   like '%${tableName}%'


        AND  a.DESCRIPTION  like '%${description}%'


        AND  a.FIELD_NAME   like '%${fieldName}%'

mapper.update:
update DATA_QUALITY_RULE a

      a.RULE_GROUP_ID = #{et.ruleGroupId}
    , a.DESCRIPTION = #{et.description}
    , a.RULE_TYPE = #{et.ruleType}
    , a.DATABASE_ID = #{et.databaseId}
    , a.SCHEMA_NAME = #{et.schemaName}
    , a.TABLE_NAME = #{et.tableName}
    , a.FIELD_NAME = #{et.fieldName}
    , a.FIELD_TYPE = #{et.fieldType}
    , a.IS_ENABLE = #{et.isEnable}
    , a.UPDATE_USER = #{et.updateUser}
    , a.UPDATE_TIME = #{et.updateTime}

where a.RULE_ID = #{et.ruleId}
