mapper.select:
select

from DATA_QUALITY_CHECK_RESULT_ERR a


    a.DEL_FLAG = 0 AND
    b.CREATE_TIME = (select max(d.CREATE_TIME) from DATA_QUALITY_CHECK_RESULT as d where b.RULE_ID = d.RULE_ID )

        AND  b.RULE_ID  = #{ruleId}


        AND  a.ORGANIZER_ID  = #{organizerId}



        AND  a.CHECK_RESULT_ID  = #{checkResultId}
