    , LINES_REJECTED
    , ERRORS
    , SETP_LOG
    , COSTTIME
    , SPEED
    , STATUS
)values(
      #{logStepId}
    , #{logTransId}
    , #{channelId}
    , #{logDate}
    , #{transname}
    , #{stepname}
    , #{stepCopy}
    , #{linesRead}
    , #{linesWritten}
    , #{linesUpdated}
    , #{linesInput}
    , #{linesOutput}
    , #{linesRejected}
    , #{errors}
    , #{setpLog}
    , #{costtime}
