mapper.select:
select

from s_data_load_t a


        AND  a.batch_no  = #{batchNo}


        AND  a.unit_no   = #{unitNo}


        AND  to_date(a.backup_time,'yyyy-MM-dd hh:mi:ss')  between to_date(#{search_satrt},'yyyy-MM-dd hh:mi:ss') and to_date(#{search_end},' yyyy-MM-dd hh:mi:ss')


        AND  to_date(a.load_time,'yyyy-MM-dd hh:mi:ss') between to_date(#{search_satrt},'yyyy-MM-dd hh:mi:ss') and to_date(#{search_end},' yyyy-MM-dd hh:mi:ss')
