mapper.select:
select

from s_data_load_t a


        AND  a.batch_no  = #{batchNo}


        AND  a.unit_no   = #{unitNo}


        AND  str_to_date(a.backup_time,'%Y%m%d%H%i%s')  between str_to_date(#{search_satrt},'%Y-%m-%d %H:%i:%s') and str_to_date(#{search_end},' %Y-%m-%d %H:%i:%s')


        AND  str_to_date(a.load_time,'%Y%m%d%H%i%s') between str_to_date(#{search_satrt},'%Y-%m-%d %H:%i:%s') and str_to_date(#{search_end},' %Y-%m-%d %H:%i:%s')
