mapper.select:
    select * from 
	(SELECT AUTH_ID,tsa.USER_ID,tsa.SERVICE_ID,SERVICE_NAME,SERVICE_URL,AUTH_IP,USERNAME,USE_DESC,USE_DEPT,USER_NAME,LINES_LIMIT,CYCLE_LIMIT,TIME_LIMIT FROM t_service_auth tsa,t_interface_user tiu,t_service_interface tsi

		tsa.USER_ID = tiu.USER_ID AND tsa.SERVICE_ID = tsi.SERVICE_ID

			AND USERNAME like '%${queryParam}%'



			AND USERNAME = #{username}



			AND tsa.USER_ID  = #{userId}


			AND tsa.SERVICE_ID  = #{serviceId}


			AND tsa.SERVICE_IDENTIFY  = #{serviceIdentify}


			AND AUTH_IP  = #{authIP}



	order by AUTH_ID desc) u limit ${start},${end}

select count(*) from (SELECT AUTH_ID ,tsa.USER_ID ,tsa.SERVICE_ID ,SERVICE_NAME ,SERVICE_URL ,AUTH_IP ,USERNAME ,USE_DESC ,USE_DEPT ,USER_NAME ,LINES_LIMIT ,CYCLE_LIMIT ,TIME_LIMIT FROM t_service_auth tsa,t_interface_user tiu,t_service_interface tsi
tsa.USER_ID = tiu.USER_ID AND tsa.SERVICE_ID = tsi.SERVICE_ID
AND USERNAME like '%${queryParam}%'
AND USERNAME = #{username}
AND tsa.USER_ID = #{userId}
AND tsa.SERVICE_ID = #{serviceId}
AND tsa.SERVICE_IDENTIFY = #{serviceIdentify}
AND AUTH_IP = #{authIP}
order by AUTH_ID desc) u

		   SELECT
			     tiu.USERNAME as userName
			   	,tsi.SERVICE_NAME as service_name
			   	,tsi.SERVICE_IDENTIFY as identify
			   	,tsa.LINES_LIMIT AS linesLimit
				,tsa.CYCLE_LIMIT AS cycleLimit
				,tsa.TIME_LIMIT  AS timeLimit
		   FROM t_service_auth tsa,t_interface_user tiu,t_service_interface tsi
           WHERE tsa.USER_ID = tiu.USER_ID AND tsa.SERVICE_ID = tsi.SERVICE_ID


				and tsa.SERVICE_ID=#{serviceId}


		  		and USERNAME=#{userName} 


		  		and SERVICE_IDENTIFY=#{identify} 

		        and AUTH_IP like #{IP}
