mapper.select:
SELECT
FROM PROFILE_TABLE_COLUMN b LEFT JOIN (select * from PROFILE_TABLE_RESULT t where not exists (select 1 from PROFILE_TABLE_RESULT where ID_PROFILE_TABLE_COLUMN=t.ID_PROFILE_TABLE_COLUMN and ID_PROFILE_TABLE_RESULT>t.ID_PROFILE_TABLE_RESULT) ) a ON b.ID_PROFILE_TABLE_COLUMN = a.ID_PROFILE_TABLE_COLUMN LEFT JOIN PROFILE_TABLE d ON d.ID_PROFILE_TABLE = b.ID_PROFILE_TABLE LEFT JOIN PROFILE_TABLE_GROUP e ON e.ID_PROFIEL_TABLE_GROUP = d.ID_PROFIEL_TABLE_GROUP LEFT JOIN R_DATABASE f ON f.ID_DATABASE = d.ID_DATABASE
AND d.ID_PROFIEL_TABLE_GROUP = #{profielTableGroup.profielTableGroupId}
AND b.ID_PROFILE_TABLE = #{profileTable.profileTableId}
AND b.ID_PROFILE_TABLE_COLUMN = #{profileTableColumn.profileTableColumnId}
ORDER BY b.ID_PROFILE_TABLE
limit ${start},${limit}

SELECT b.indicatorAllCount ,b.indicatorNullCount,a.CREATE_TIME createTime FROM profile_table_result a, (SELECT ID_PROFILE_TABLE_COLUMN, AVG(INDICATOR_ALL_COUNT) AS indicatorAllCount, AVG(INDICATOR_NULL_COUNT)AS indicatorNullCount, MAX(CREATE_TIME) max_day FROM profile_table_result GROUP BY ID_PROFILE_TABLE_COLUMN,DATE_FORMAT(CREATE_TIME,'%m-%d-%Y')) b ,PROFILE_TABLE_COLUMN cl WHERE a.ID_PROFILE_TABLE_COLUMN = b.ID_PROFILE_TABLE_COLUMN AND a.CREATE_TIME = b.max_day AND a.ID_PROFILE_TABLE_COLUMN = cl.ID_PROFILE_TABLE_COLUMN
AND cl.PROFILE_TABLE_COLUMN_NAME = #{profileTableColumn.profileTableColumnName}
ORDER BY a.CREATE_TIME limit 0,10

SELECT COUNT(*) FROM PROFILE_TABLE_COLUMN b LEFT JOIN (select * from PROFILE_TABLE_RESULT t where not exists (select 1 from PROFILE_TABLE_RESULT where ID_PROFILE_TABLE_COLUMN=t.ID_PROFILE_TABLE_COLUMN and ID_PROFILE_TABLE_RESULT>t.ID_PROFILE_TABLE_RESULT) ) a ON b.ID_PROFILE_TABLE_COLUMN = a.ID_PROFILE_TABLE_COLUMN LEFT JOIN PROFILE_TABLE d ON d.ID_PROFILE_TABLE = b.ID_PROFILE_TABLE LEFT JOIN PROFILE_TABLE_GROUP e ON e.ID_PROFIEL_TABLE_GROUP = d.ID_PROFIEL_TABLE_GROUP LEFT JOIN R_DATABASE f ON f.ID_DATABASE = d.ID_DATABASE
AND d.ID_PROFIEL_TABLE_GROUP = #{profielTableGroup.profielTableGroupId}
AND b.ID_PROFILE_TABLE = #{profileTable.profileTableId}
AND b.ID_PROFILE_TABLE_COLUMN = #{profileTableColumn.profileTableColumnId}
